document.addEventListener('DOMContentLoaded', () => {
    // Get references to important DOM elements
    const imageContainer = document.querySelector('main');
    const feedbackForm = document.getElementById('feedback-form');
    const feedbackType = document.getElementById('feedback-type');

    // Populate feedback dropdown with options
    const feedbackOptions = ['General Feedback', 'App Enhancement', 'General Comment'];
    feedbackOptions.forEach(option => {
        const optionElement = document.createElement('option');
        optionElement.value = option.toLowerCase().replace(' ', '-');
        optionElement.textContent = option;
        feedbackType.appendChild(optionElement);
    });

    // Sample image data - in a real application, this would likely come from a server
    const images = [
        { id: 1, url: 'data:image/jpeg;base64,', caption: 'Spooderman', liked: false, hidden: false },
        { id: 2, url: 'data:image/jpeg;base64,', caption: 'Minecraft Steve', liked: false, hidden: false },
        { id: 3, url: 'data:image/jpeg;base64,', caption: 'Feet', liked: false, hidden: false },
    ];

    // Function to update existing images and add action buttons
    function updateImages() {
        images.forEach((image, index) => {
            const figure = document.getElementById(`image${index + 1}`);
            if (figure) {
                // Update image source and alt text
                const img = figure.querySelector('img');
                img.src = image.url;
                img.alt = image.caption;
                
                // Add image actions if they don't exist
                if (!figure.querySelector('.image-actions')) {
                    const actionsDiv = document.createElement('div');
                    actionsDiv.className = 'image-actions';
                    actionsDiv.innerHTML = `
                        <button class="like-btn" data-id="${image.id}">${image.liked ? 'Unlike' : 'Like'}</button>
                        <button class="hide-btn" data-id="${image.id}">Hide</button>
                        <button class="share-btn" data-id="${image.id}">Share</button>
                    `;
                    figure.appendChild(actionsDiv);
                }

                // Show or hide the image based on its 'hidden' status
                figure.style.display = image.hidden ? 'none' : 'block';
            }
        });
    }

    // Event delegation for image actions (like, hide, share)
    imageContainer.addEventListener('click', (e) => {
        if (e.target.classList.contains('like-btn') || 
            e.target.classList.contains('hide-btn') || 
            e.target.classList.contains('share-btn')) {
            
            const imageId = parseInt(e.target.dataset.id);
            const image = images.find(img => img.id === imageId);

            if (e.target.classList.contains('like-btn')) {
                // Toggle like status
                image.liked = !image.liked;
                e.target.textContent = image.liked ? 'Unlike' : 'Like';
            } else if (e.target.classList.contains('hide-btn')) {
                // Hide the image
                image.hidden = true;
                updateImages();
            } else if (e.target.classList.contains('share-btn')) {
                // Placeholder for share functionality
                alert(`Sharing image ${imageId}`);
            }
        }
    });

    // Handle feedback form submission
    feedbackForm.addEventListener('submit', (e) => {
        e.preventDefault();
        const feedbackText = document.getElementById('feedback-text').value;
        // Placeholder for feedback submission - in a real app, this would send data to a server
        alert(`Feedback submitted: ${feedbackType.value} - ${feedbackText}`);
        feedbackForm.reset();
    });

    // Initial update of images when the page loads
    updateImages();
});